css:
*{
margin: 0%;
padding: 0%;
font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
}

.background {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;

    display: flex;
    justify-content: center;
    align-items: center;
    opacity: 0.1;
  }

   .row{
       display:flex;
   }

   .container{
       width:100%;
margin-top: 100px;
   }
   .quot_color{
       color:#0f4a7d;
   }

   .address{
margin-top: 20px;
margin-left: 15px;

   }
   .col-3{
       width: 30%;
   }

   .col-4{
       width: 40%;
   }
   .col-5{
       width: 50%;
   }

   .col-6{
       width: 60%;
   }
   .table{
    margin-top: 20px;
   }
   .table p{
    margin-top: 20px;
    font-weight: bold;
   }
.trow{
    background-color:#0f4a7d;
    text-align:center;
    color:white;
    font-weight: bold;
}
.text-center{
    text-align:center;
}
.bottom_text{
    color:#0f4a7d;
}
.company{
    margin-left:20px;
}
