css:
@page {
    margin: 0;

  }
 * {
    margin: 0%;
    padding: 0%;
    font-family: "Gill Sans", "Gill Sans MT", Calibri, "Trebuchet MS",
      sans-serif;
  }
  @media print{
      .mt-50{
    margin-top:150px;
}
  }

  .background {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 0;
  }

  .background img {
    width: 100vw;
    height: 100vh;
  }

  .flex {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  .print-format {
    width: 100%;
    padding: 0 15px;
    background-color: transparent;
  }

  .print-format > table {
    width: 100%;
    position: relative;
    z-index: 1000;
  }

  .table1 {
    margin-bottom: 20px;
  }

  .table2 {
    margin-bottom: 20px;
  }



.items th {

    background-color: #13395a !important;
    color:white !important;
}

table { page-break-after:auto }
tr    { page-break-inside:always; page-break-after:always }
td    { page-break-inside:always; page-break-after:auto }
thead { display: table-header-group; }
tfoot { display: table-footer-group;}
