css:
*{
 margin:0;
 padding:0; 
 font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;  
 font-size:16px;
}

.background {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    
    display: flex;
    justify-content: center;
    align-items: center;
    opacity: 0.3;
}

.container-fluid{
    display: flex;
    width: 100%;
    height: max-content;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
}

.party-info {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.col-md-4 img
{
    margin-top: 80px;
    margin-right: 10px;
}
.col-md-6 h4
{
    margin-top: -25px;
}
#logo
{
    text-align: right;
}
.col-md-12
{
    text-align: right;
}
