css:
* {
  margin: 0;
  padding: 0;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
  font-size: 16px;
}

.background {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  opacity: 0.1;
}

.background img {
  transform: rotate(-50deg);
  width: 100%;
  height: auto;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}

.party-info {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-top:40px;

}

.row {
  margin-left:5px;
  margin-right:5px;
  margin-top:40px;
  display:flex;
  justify-content:space-between;
}

.column {
  float: left;
  width: 45%;

}


.column table {
  border-collapse: collapse;
  border-spacing: 0;   
  width: 100%;


}




.intro{
    margin-top:40px;
    background-color:white;


}
.intro table{
    width:93%;

}
