css:
* {
   margin: 0;
   padding: 0;
   font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
   font-size: 14px;
 }

 .background {
   position: fixed;
   top: 0;
   bottom: 0;
   left: 0;
   right: 0;

   display: flex;
   justify-content: center;
   align-items: center;
   opacity: 0.1;
 }

 .background img {
   transform: rotate(-50deg);
   width: 100%;
   height: auto;
 }
 * {
   box-sizing: border-box;
   margin: 0;
   padding: 0;
 }

 .row {
   margin-left:-5px;
   margin-right:-5px;
 }

 .column {
   float: left;
   width: 50%;
   padding: 5px;
 }
 .row::after {
   content: "";
   clear: both;
   display: table;
 }

 table {
   border-collapse: collapse;
   border-spacing: 0;
   width: 100%;
   border: 1px solid #ddd;
 }

 th, td {
   text-align: left;
   padding: 16px;
 }

 tr:nth-child(even) {
   background-color: #f2f2f2;
 }
