css:
* {
  margin: 0;
  padding: 0;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
  font-size: 14px;
}

.background {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;

  display: flex;
  justify-content: center;
  align-items: center;
  opacity: 0.1;
}

.background img {
  transform: rotate(-50deg);
  width: 100%;
  height: auto;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}

.party-info {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
}

.table {
  width: 100%;
  font-size: 12px;
}

.table td {
  text-align: left;  
}

.totals .table {
  width: max-content;
  margin-left: auto;
}
